import '../styles/App.scss';
import '../styles/page.scss';
import { useState } from 'react';

function App() {

  const [tasks, setTasks] = useState([
    { id: '0', task: 'Comprar harina, jamÃ³n y pan rallado', completed: true },
    { id: '1',task: 'Hacer croquetas ricas', completed: true },
    { id: '2',task: 'Ir a la puerta de un gimnasio', completed: false },
    { id: '3',task: 'Comerme las croquetas mirando a la gente que entra en el gimnasio', completed: false }
  ]);




  const handleCompleted =(ev)=> {
    const clickedTask = ev.currentTarget.id;

    const foundTask = tasks.find ((oneTask) => oneTask.id === clickedTask);
    foundTask.completed = !foundTask.completed ;
    setTasks([...tasks]);

  }

  const renderTasks = () => {
    return tasks.map((task, index) => {
        return (<li className={task.completed ? 'completed' : ''} key={index} id={task.id} onClick={handleCompleted}>{task.task}</li>)


    });

  };

  return (
    <div className="App">
      <h1>Mi lista de tareas</h1>
      <ol>
        {renderTasks()}
      </ol>
    </div>
  );
}

export default App;
