import '../styles/app.scss';

import { useState, useEffect } from 'react';
import {  Route, Switch, useRouteMatch } from 'react-router-dom';

import getCharactersFromApi from './services/Api';
import Header from './Header';
import CharacterList from './CharacterList';
import Filter from './Filter';
import CharacterDetail from './CharacterDetail';

function App() {

  const [data , setData] = useState([]);
  const [inputSearch , setInputSearch] = useState ('');
  const [message , setMessage] = useState ('hidden');


useEffect(() => {
    getCharactersFromApi()
      .then( (data) => {
        setData(data)
    });
  } , []);


const routerData= useRouteMatch('/character/:id');
console.log(routerData);
const characterId = routerData !== null ? routerData.params.id : '';
console.log(characterId);

const selectedCharacter = data.find((char)=>{
  return (parseInt(char.id )===parseInt(characterId));
})
console.log(selectedCharacter);







  const handleSearch = (value) =>{


    return setInputSearch(value);

  }


  const filteredData = data
    .filter((character)=>{

        return character.name.toLocaleLowerCase().includes(inputSearch.toLocaleLowerCase()) ? setMessage('hidden'): setMessage('');

    })






  return (
    <div className="page">

    <Header />
    <Switch>
    <main>
      <Route exact path='/'>
      <section>
        <Filter handleSearch={handleSearch} value={inputSearch} message={message} />
        <CharacterList data={filteredData}   />
       </section>
       </Route>
       <Route path='/character/:id'>

          <CharacterDetail selectedCharacter={selectedCharacter} />


       </Route>
    </main>

    </Switch>

  </div>
  );
}

export default App;
