import '../styles/App.scss';
import '../styles/page.scss';
import { useState } from 'react';

function App() {

  const [tasks, setTasks] = useState([
    { task: 'Comprar harina, jamÃ³n y pan rallado', completed: true },
    { task: 'Hacer croquetas ricas', completed: true },
    { task: 'Ir a la puerta de un gimnasio', completed: false },
    { task: 'Comerme las croquetas mirando a la gente que entra en el gimnasio', completed: false }
  ]);



  const handleCompleted =(ev)=> {
    const clickedTask = ev.currentTarget.completed;

    const foundTask = tasks.find (task => task.completed === clickedTask);
    foundTask.completed = !foundTask.completed ;
    setTasks([...tasks]);

  }

  const renderTasks = () => {
    return tasks.map((task, index) => {
        return (<li key={index} id={index} onClick={handleCompleted}>{task.task}</li>)


    });

  };

  return (
    <div className="App">
      <h1>Mi lista de tareas</h1>
      <ol>
        {renderTasks()}
      </ol>
    </div>
  );
}

export default App;
